import {
  Container,
  Row,
  Col,
  Alert,
  Breadcrumb,
  BreadcrumbItem,
  Card,
  CardHeader,
  CardTitle,
  CardImg,
  CardBody,
  CardFooter,
  Button
} from "shards-react";
import ScrollReveal from "scrollreveal";
import LazyLoad from "react-lazyload";

class MeetAna extends React.Component {
  componentDidMount() {
    ScrollReveal().reveal(".headline");
  }

  render() {
    return (
      <div
        className="text-white"
        style={{ backgroundColor: " #ff99ff", padding: "40px" }}
      >
        <Row>
          <Col sm={12} md={6} lg={6} xl={6}>
            <br />
            <br />
            <br />
            <h1 className="headline text-white">Meet Ana</h1>
            <br />
            <br />
            <p>Meet Ana</p>

            <div className="audio">
              <audio controls>
                <source src="https://dl.dropbox.com/s/7ofviet2398fz0b/Example%201%20-%20Ana.mp3?dl=0" />
              </audio>
            </div>
          </Col>
          <Col sm={12} md={6} lg={6} xl={6}>
            <a href="https://imgur.com/ociFUp7">
              <img
                style={{ width: "100%" }}
                src="https://i.imgur.com/ociFUp7.png"
                title="source: imgur.com"
              />
            </a>
          </Col>
        </Row>
      </div>
    );
  }
}

export default MeetAna;
