import { StickyContainer, Sticky } from "react-sticky/lib";
import {
  Container,
  BreadcrumbItem,
  Row,
  Col,
  Breadcrumb,
  Card,
  CardHeader,
  CardTitle,
  CardImg,
  CardBody,
  CardFooter,
  Button,
  Form,
  FormInput,
  FormGroup
} from "shards-react";

class GuideLayout extends React.Component {
  render() {
    return (
      <div>
        <Row>
          <StickyContainer>
            <Col sm={12} md={8} lg={8} xl={8}></Col>
            <Col sm={12} md={4} lg={4} xl={4}>
              <Sticky>
                {({
                  style,

                  // the following are also available but unused in this example
                  isSticky,
                  wasSticky,
                  distanceFromTop,
                  distanceFromBottom,
                  calculatedHeight
                }) => (
                  <header style={style}>
                    <h1>Hello</h1>
                  </header>
                )}
              </Sticky>
            </Col>
          </StickyContainer>
        </Row>
      </div>
    );
  }
}

export default GuideLayout;
