import {
  Container,
  Row,
  Col,
  Alert,
  Breadcrumb,
  BreadcrumbItem,
  Card,
  CardHeader,
  CardTitle,
  CardImg,
  CardBody,
  CardFooter,
  Button
} from "shards-react";
import Head from "next/head";
import SEO from "react-seo-component";

import Layout from "../components/Layout";

// Style for the onft icons
const liStyles = {
  display: "inline!important",
  paddingLeft: "50px"
};

const oLiStyles = {
  display: "inline"
};

const ulStyles = {
  listStyleType: "none",
  paddingTop: "15px"
};

const Index = props => (
  <Layout>
    <SEO
      title="Contact | CXC Spanish Guide"
      description={
        "Contact Us. Whether you're a beginner or just looking for some refreshers, we want to express gratitue for letting us be a part of your journey."
      }
      image={""}
      pathname={"https://www.cxcspanish.com/contact"}
      siteLanguage={"en"}
      siteLocale={""}
      twitterUsername={"cxcspanish"}
    />
    <Container>
      <Breadcrumb>
        <BreadcrumbItem>
          <a href="/">Home</a>
        </BreadcrumbItem>
        <BreadcrumbItem active>Contact</BreadcrumbItem>
      </Breadcrumb>
      <Row>
        <Col sm={12} md={8} lg={8}>
          <Card>
            <CardBody>
              <CardTitle>Contact Us</CardTitle>

              <br />
              <p>
                Whether you're a beginner or just looking for some refreshers,
                we want to express gratitude for letting us be a part of your
                journey.
              </p>

              <p>
                Don't forget to read our <a href="/FAQ">FAQ</a>.
              </p>
            </CardBody>
          </Card>
        </Col>
        <Col sm={12} md={4} lg={4}></Col>
      </Row>
    </Container>
  </Layout>
);

export default Index;
