import {
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  FormInput,
  Collapse
} from "shards-react";
import FA from "react-fontawesome";

// CSS
import "bootstrap/dist/css/bootstrap.css";
import CSS from "shards-ui/dist/css/shards.css";

class NavbarComponent extends React.Component {
  constructor(props) {
    super(props);

    this.toggleDropdown = this.toggleDropdown.bind(this);
    this.toggleNavbar = this.toggleNavbar.bind(this);

    this.state = {
      title: "CXC Spanish Online",
      dropdownOpen: false,
      collapseOpen: false
    };
  }

  toggleDropdown() {
    this.setState({
      ...this.state,
      ...{
        dropdownOpen: !this.state.dropdownOpen
      }
    });
  }

  toggleNavbar() {
    this.setState({
      ...this.state,
      ...{
        collapseOpen: !this.state.collapseOpen
      }
    });
  }
  render() {
    return (
      <Navbar type="dark" theme="primary" expand="md">
        <NavbarBrand href="/">{this.state.title}</NavbarBrand>
        <NavbarToggler onClick={this.toggleNavbar} />

        <Collapse open={this.state.collapseOpen} navbar>
          <Nav navbar>
            <NavItem>
              <NavLink href="#">Guides</NavLink>
            </NavItem>

            {/* Exam Topics */}
            <Dropdown
              open={this.state.dropdownOpen}
              toggle={this.toggleDropdown}
            >
              <DropdownToggle nav caret>
                Exam Topics
              </DropdownToggle>
              <DropdownMenu>
                <DropdownItem>Directed Situations</DropdownItem>
                <DropdownItem>Letter Writing</DropdownItem>
                <DropdownItem>Composition</DropdownItem>
                <DropdownItem>Contextual Announcements</DropdownItem>
                <DropdownItem>Contextual Dialogue</DropdownItem>
                <DropdownItem>Reading Comprehension</DropdownItem>
                <DropdownItem>Directed Situations</DropdownItem>
              </DropdownMenu>
            </Dropdown>
            <NavItem>
              <NavLink href="#">Verb Tenses</NavLink>
            </NavItem>

            <NavItem>
              <NavLink href="#">Resources</NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="#">Quiz</NavLink>
            </NavItem>
          </Nav>

          <Nav navbar className="ml-auto">
            <NavItem>
              <NavLink href="#">
                <FA style={{ padding: "7px" }} name="users" />
                About Us
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="#">
                <FA style={{ padding: "7px" }} name="chalkboard-teacher" />
                Tutoring
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="#">
                <FA style={{ padding: "7px" }} name="comment" />
                FAQ
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="#">
                <FA style={{ padding: "7px" }} name="id-badge" />
                Contact
              </NavLink>
            </NavItem>
          </Nav>
        </Collapse>
      </Navbar>
    );
  }
}

export default NavbarComponent;
