import {
  Container,
  Row,
  Col,
  Alert,
  Breadcrumb,
  BreadcrumbItem,
  Card,
  CardHeader,
  CardTitle,
  CardImg,
  CardBody,
  CardFooter,
  Button
} from "shards-react";
import ScrollReveal from "scrollreveal";
import LazyLoad from "react-lazyload";
import Collapsible from "react-collapsible";

class MeetAna extends React.Component {
  componentDidMount() {
    ScrollReveal().reveal(".anna-image");
    ScrollReveal().reveal(".anna-description");
    ScrollReveal().reveal(".headline");
    ScrollReveal().reveal(".anna-audio");
  }

  render() {
    return (
      <div
        className="text-white"
        style={{ backgroundColor: " #ff99ff", padding: "40px" }}
      >
        <Row>
          <Col sm={12} md={6} lg={6} xl={6}>
            <Container>
              <br />
              <br />
              <br />
              <h1 className="headline text-white">Meet Ana</h1>
              <br />
              <br />
              <p>
                Meet Ana, a young and charismatic woman living in Spain. Let's
                listen as she introduces herself, talks about what it's like
                living in there, her experiences adn family life.
              </p>

              <div className="anna-audio">
                <audio controls>
                  <source src="https://dl.dropbox.com/s/7ofviet2398fz0b/Example%201%20-%20Ana.mp3?dl=0" />
                </audio>
              </div>

              <br />
              <Collapsible
                trigger={
                  <a className="text-white" href="">
                    Click here to read transcript
                  </a>
                }
              >
                <p></p>
              </Collapsible>
            </Container>
          </Col>
          <Col sm={12} md={6} lg={6} xl={6}>
            <br className="d-sm-none" />
            <a className="anna-image" href="https://imgur.com/ociFUp7">
              <img
                style={{ width: "100%" }}
                src="https://i.imgur.com/ociFUp7.png"
                title="source: imgur.com"
              />
            </a>
          </Col>
        </Row>
      </div>
    );
  }
}

export default MeetAna;
