      <li>
        <PDFFIle href="" title="Common Spanish Adverbs" />
      </li>
      <li>
        <PDFFIle href="" title="Verb Conjugation Refresher" />
      </li>
    </ul>

    <style>
      {`
      .list-ul {
        padding-left: 20px;
        margin-top: -10px;
      }

      .list-ul li {
        padding-bottom: 10px;
      }
      `}
    </style>
  </div>
);

export default UsefulEssentials;
