import {
  Container,
  BreadcrumbItem,
  Row,
  Col,
  Breadcrumb,
  Card,
  CardHeader,
  CardTitle,
  CardImg,
  CardBody,
  CardFooter,
  Button,
  Form,
  FormInput,
  FormGroup,
  Collapse,
  Alert,
  Badge
} from "shards-react";
import Head from "next/head";
import {
  BrowserView,
  MobileView,
  isBrowser,
  isMobile
} from "react-device-detect";
import SEO from "react-seo-component";
import ResourcesLayout from "../resources-components/ResourcesLayout";

// Layout
import Layout from "../components/Layout";
import Img from "react-image";
import Collapsible from "react-collapsible";

// Common
import PDFFile from "../common/PDFFile";

const CardBox = props => (
  <div>
    <br />

    <Row>
      <Col sm={12} md={6} lg={6} xl={6}>
        <Card>
          <CardImg
            style={{ width: "100%" }}
            src="https://static.whatsapp.net/rsrc.php/v3/yO/r/FsWUqRoOsPu.png"
          />
          <CardBody>
            <CardTitle>Join Us on WhatsApp</CardTitle>

            <p>
              Speak with us live, make friends, ask questions, participate in
              our weekly exercises and more.
            </p>
          </CardBody>
        </Card>
      </Col>
      <Col sm={12} md={6} lg={6} xl={6}>
        <br className="d-sm-none" />
        <Card>
          <CardImg
            style={{ width: "100%" }}
            src="https://cdn.images.express.co.uk/img/dynamic/59/590x/Instagram-logo-1011468.jpg?r=1535748410906"
          />
          <CardBody></CardBody>
        </Card>
      </Col>
    </Row>
  </div>
);

export default CardBox;
