import {
  Container,
  Row,
  Col,
  Alert,
  Breadcrumb,
  BreadcrumbItem,
  Card,
  CardHeader,
  CardTitle,
  CardImg,
  CardBody,
  CardFooter,
  Button
} from "shards-react";
import { Modal, ModalBody, ModalHeader } from "shards-react";

class QuickSurvey extends React.Component {
  constructor(props) {
    super(props);
    this.state = { open: false };
    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    this.setState({
      open: !this.state.open
    });
  }

  render() {
    const { open } = this.state;

    return (
      <div className="bg-dark" style={{ height: "auto", padding: "10px 0px" }}>
        <Container>
          <h3 className="text-white" style={{ margin: "auto" }}>
            Is there something in Spanish you'd like us to clarify? Let us know{" "}
            <a href="#" onClick={this.toggle}>
              here
            </a>
          </h3>
        </Container>
        <Modal
          style={{ width: "auto", height: "auto" }}
          size="lg"
          open={open}
          toggle={this.toggle}
        >
          <iframe
            src="https://docs.google.com/forms/d/e/1FAIpQLSc0SyNaXGAYnToalhKErIwH4b9deZQw-mA1zYJ491UU-robIQ/viewform?embedded=true"
            width="1000"
            height="1028"
            frameborder="0"
            marginheight="0"
            marginwidth="0"
          >
            Loadingâ€¦
          </iframe>
        </Modal>
      </div>
    );
  }
}

export default QuickSurvey;
