import StickyBox from "react-sticky-box";

class GuideLayout extends React.Component {
  render() {
    return (
      <div>
        <div className="row">
          <StickyBox offsetTop={20} offsetBottom={20}>
            <div style={{ border: "3px solid green" }}>Sidebar</div>
          </StickyBox>
          <div>Content</div>
        </div>
      </div>
    );
  }
}

export default GuideLayout;
