import NavbarComponent from "./NavbarComponent";
import Footer from "./Footer";
import { Card, Container, Alert } from "shards-react";
import HeaderImports from "./HeaderImports";
import React from "react";

// Assets
import "bootstrap/dist/css/bootstrap.css";
import CSS from "shards-ui/dist/css/shards.css";
import "../assets/css/carousel.css";
import "../assets/css/nf-carousel.css";
import "../assets/css/master.css";

// Javascript
import "../assets/js/fontawesome.js";
import "../assets/js/mixpanel.js";
import "../assets/js/quantcast.js";
import "../assets/js/facebook.js";

// Other
import ReactGA from "react-ga";
import MessengerCustomerChat from "react-messenger-customer-chat";

// Google Analytics
ReactGA.initialize("UA-146229442-1");

// Render on client side
if (process.browser) {
  ReactGA.initialize("UA-146229442-1", {
    debug: true,
    titleCase: false,
    gaOptions: {
      userId: 123
    }
  });

  ReactGA.pageview(window.location.pathname + window.location.search);
}

const Fragment = React.Fragment;

// Style for the onft icons
const liStyles = {
  display: "inline!important",
  paddingLeft: "50px"
};

const oLiStyles = {
  display: "inline"
};

const ulStyles = {
  listStyleType: "none",
  paddingTop: "15px"
};

const Layout = props => (
  <Fragment>
    <MessengerCustomerChat
      pageId="<https://cxcspanish.cxcspanish.now.sh>"
      appId="669007593616991"
      htmlRef="https://connect.facebook.net/en_US/sdk/xfbml.customerchat.js"
    />
    <div
      class="fb-customerchat"
      attribution="setup_tool"
      page_id="105703077445673"
    ></div>
    <HeaderImports />
    <NavbarComponent />

    <br />
    <Alert theme="danger">
      Please note that this is the beta version of the website and some contents
      have been disabled or may not work properly. -{" "}
      <a className="alert-link" href="/FAQ">
        See FAQ
      </a>
      <br />
      Please report all errors to{" "}
      <a className="alert-link" href="mailto:hola@cxcspanish.com">
        hola@cxcspanish.com
      </a>
    </Alert>
    <br />
    <Fragment>{props.children}</Fragment>
    <br />
    <Card>
      <span className="border border-light">
        <ul style={ulStyles}>
          <li style={oLiStyles}>
            <i style={{ color: "#8a3ab9" }} className="fab fa-instagram" />
            <a
              className="text-muted"
              style={{ paddingLeft: "10px" }}
              href=""
              target="_blank"
            >
              cxcspanish
            </a>
          </li>
          <li style={liStyles}>
            <i style={{ color: " #3b5998" }} className="fab fa-facebook" />
            <a
              className="text-muted"
              style={{ paddingLeft: "10px" }}
              href=""
              target="_blank"
            >
              CXC Spanish Gude
            </a>
          </li>
          <li style={liStyles}>
            <i style={{ color: "#00acee" }} className="fab fa-twitter" />
            <a
              className="text-muted"
              style={{ paddingLeft: "10px" }}
              href=""
              target="_blank"
            >
              cxcspanish
            </a>
          </li>
        </ul>
      </span>
    </Card>
    <br />
    <Footer />
  </Fragment>
);

export default Layout;
