import {
  Container,
  BreadcrumbItem,
  Row,
  Col,
  Breadcrumb,
  Card,
  CardHeader,
  CardTitle,
  CardImg,
  CardBody,
  CardFooter,
  Button,
  Form,
  FormInput,
  FormGroup
} from "shards-react";
import Head from "next/head";
import SEO from "react-seo-component";

// Layout
import Layout from "../../components/Layout";
import GuideLayout from "../../guide-components/GuideLayout";

const PorVsPara = props => (
  <React.Fragment>
    <SEO
      title="Por Vs. Para | CXC Spanish Guide"
      description={
        "Learn everything you need to know about these two essential words and how to use them."
      }
      image={""}
      pathname={"https://www.cxcspanish.com/guides/"}
      siteLanguage={"en"}
      siteLocale={""}
      twitterUsername={"_rxheem"}
    />
    <Layout>
      <Container>
        <Breadcrumb>
          <BreadcrumbItem>
            <a href="/">Home</a>
          </BreadcrumbItem>
          <BreadcrumbItem>
            <a href="/guide">Guides</a>
          </BreadcrumbItem>
          <BreadcrumbItem active></BreadcrumbItem>
        </Breadcrumb>
        <GuideLayout>
          <Card>
            <CardHeader>Card header</CardHeader>
            <CardImg src="https://place-hold.it/300x200" />
            <CardBody>
              <CardTitle>Por vs. Para</CardTitle>
              <p>Lorem ipsum dolor sit amet.</p>
              <Button>Read more &rarr;</Button>
            </CardBody>
            <CardFooter>Card footer</CardFooter>
          </Card>
        </GuideLayout>
      </Container>
    </Layout>
  </React.Fragment>
);

export default PorVsPara;
