import {
  Container,
  Row,
  Col,
  Alert,
  Breadcrumb,
  BreadcrumbItem,
  Card,
  CardHeader,
  CardTitle,
  CardImg,
  CardBody,
  CardFooter,
  Button
} from "shards-react";
import Head from "next/head";

// Home components
import HomeCarousel from "../home/HomeCarousel";
import Marketing from "../home/Marketing";
import MailingListSubscribe from "../home/MailingListSubscribe";

// Layout
import Layout from "../components/Layout";
import Message from "../common/Message";

function Error({ statusCode }) {
  return (
    <Layout>
      <Container>
        <Head>
          <title>404 Page Not Found | CXC Spanish Guide</title>
          <meta
            name="og:title"
            content="404 Page Not Found | CXC Spanish Guide"
          />
          <meta name="og:site_name" content="CXC Spanish Online" />
          <meta
            name="description"
            content="We couldn't finf the page you were lookign for. Let us help you."
          />
        </Head>

        <Breadcrumb>
          <BreadcrumbItem>
            <a href="/">Home</a>
          </BreadcrumbItem>
          <BreadcrumbItem active>404 - Page Not Found</BreadcrumbItem>
        </Breadcrumb>
      </Container>
    </Layout>
  );
}

Error.getInitialProps = ({ res, err }) => {
  const statusCode = res ? res.statusCode : err ? err.statusCode : 404;
  return { statusCode };
};

export default Error;
