import { Nav, NavItem, NavLink } from "shards-react";
import {
  BrowserView,
  MobileView,
  isBrowser,
  isMobile
} from "react-device-detect";

function MiniNav() {
  return (
    <MobileView>
      <Nav fill className="bg-light">
        <NavItem>
          <NavLink active href="#">
            Orals Practice
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink href="#">Listening</NavLink>
        </NavItem>

        <NavItem>
          <NavLink href="/past-papers">Past Papers</NavLink>
        </NavItem>
      </Nav>
    </MobileView>
  );
}

export default MiniNav;
